import React, { Component } from 'react'



function SelectLang(props){
    const languages = ["All","Javascript", "Python", "Ruby", "Java"];
    return (
        <ul className="languages">
            {languages.map((elem) => {
                return (

                    <li key={elem}
                    style={elem===this.state.selectedlang ? {color:'#d0021b'}:null}
                    onClick={this.updatedlang.bind(null,elem)}
                    >
                        {elem}
                    </li>
                )
            })}

        </ul>
    )
}





export class Popular extends Component {
    constructor(){
        super();
        this.state={
            selectedlang:"All"
        }
        this.updatedlang=this.updatedlang.bind(this);
    }

    updatedlang(lang){
        this.setState(function(){
            return {selectedlang:lang}
        });
    }


    render() {
        const languages = ["All","Javascript", "Python", "Ruby", "Java"];
        return (
            <ul className="languages">
                {languages.map((elem) => {
                    return (

                        <li key={elem}
                        style={elem===this.state.selectedlang ? {color:'#d0021b'}:null}
                        onClick={this.updatedlang.bind(null,elem)}
                        >
                            {elem}
                        </li>
                    )
                })}

            </ul>
        )
    }
}

export default Popular
