import React, { Component } from 'react'

export class Popular extends Component {
    constructor(){
        super();
        this.state={
            selectedlang:"All"
        }
        this.updatedlang=this.updatedlang.bind(this);
    }

    updatedlang(lang){
        this.setState(function(){
            return {selectedlang:lang}
        });
    }


    render() {
        const languages = ["Javascript", "Python", "Ruby", "Java"];
        console.log("up here ",this);

        return (
            <ul className="languages">
                 <h1>Selected lang is : {this.state.selectedlang}</h1>
                {languages.map((elem) => {
                    console.log("down here",this)
                    return (

                        <li key={elem}
                        onClick={this.updatedlang.bind(null,elem)}
                        >
                            {elem}
                        </li>
                    )
                })}

            </ul>
        )
    }
}

export default Popular
